<style>
  .menu {
    --narrow: 1;

    position: relative;
    font-size: var(--framework-font-size);
    z-index: 2;
    user-select: none;
  }

  @media (width <= 80rem) {
    .menu:is(.open, :target) {
      position: fixed;
      inset: 0;
      overflow: auto;
      background-color: var(--background-color);
      color: var(--text-color);
    }

    :root:has(.menu:is(.open, :target)) {
      overflow: hidden;
    }
  }

  .content {
    display: flex;
    align-items: center;
    gap: 2rem;
    width: 100%;
    font-family: var(--sans-serif);
  }

  @media (width <= 80rem) {
    .menu:is(.open, :target) .content {
      flex-wrap: wrap;
      padding: clamp(1rem, var(--page-gutter), 2.25rem) var(--page-gutter);
      font-size: 1rem;
    }
  }

  .home {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: min(1.4rem, 4vw);
    margin-right: auto;
  }

  .divider {
    align-self: stretch;
    border-right: 1px solid currentcolor;
  }

  .mesh {
    display: none;
  }

  .idea {
    height: 3rem;
  }

  .gsdi {
    transform: translateX(-38.5%);

    /* Pull in menu close button */
    margin-right: -5rem; /* 1 */
  }

  @media (width > 30rem) {
    .mesh {
      display: initial;
    }

    .gsdi {
      transform: none;
      margin-right: 0;
    }
  }

  .toggle.close {
    display: none;
  }

  .menu:is(.open, :target) .toggle.open {
    display: none;
  }

  .menu:is(.open, :target) .toggle.close {
    display: block;
  }

  .icon {
    width: 3rem;
    height: 3rem;
    padding: 0.5rem;
    margin: -0.5rem;
  }

  @media (width > 80rem) {
    .menu:is(:is(.open, :target), :not(:is(.open, :target)))
      .toggle:is(.open, .close) {
      display: none;
    }
  }

  .items {
    display: flex;
    flex-direction: column;
    gap: clamp(1.3rem, 2vw, 2rem);
  }

  @media (width <= 80rem) {
    .items {
      flex-direction: column;
      gap: 1.25rem;
      width: 100%;
      height: 100%;
      padding: min(10vh, 5rem) 0 0;
    }

    .menu:not(:is(.open, :target)) .items {
      display: none;
    }
  }

  @media (width > 80rem) {
    .items {
      flex-direction: row;
    }
  }

  .item {
    position: relative;
  }

  .anchor {
    display: flex;
    align-items: center;
    gap: 0.15rem;
    padding: 0.25rem 0.5rem;
    margin: -0.25rem -0.5rem;
  }

  .anchor[href]:hover {
    text-decoration: underline;
  }

  @media (width > 80rem) {
    .anchor {
      white-space: nowrap;
    }
  }

  .anchor.active {
    font-weight: var(--sans-serif-bold);
  }

  .anchor.parent {
    position: relative;
  }

  .chevron {
    width: 1.2em;
    margin: 0 -0.4em 0 -0.1em;
    height: auto;
  }

  .children {
    padding: 1rem 0 0 1.5rem;
    position: relative;
    z-index: 1;
  }

  @media (width > 80rem) {
    .children {
      display: none;
      min-width: 12rem;
      padding: 1rem;
      position: absolute;
      left: 0;
      top: calc(100% + 0.75rem);
      border-radius: var(--border-radius);
      background: var(--background-color);
      box-shadow: 0 0.25rem 1rem rgba(0, 0, 0, 0.1),
        0 0 0 1px rgba(0, 0, 0, 0.05);
      transform-origin: center;
      animation: slide-in 175ms forwards cubic-bezier(0.4, 0, 0.2, 1);
    }

    .children:before {
      content: '';
      position: absolute;
      inset: auto 0 100%;
      height: 1rem;
    }

    .children .anchor {
      color: var(--text-color);
    }

    .item:hover > .children,
    .item:focus-within > .children {
      display: flex;
      flex-direction: column;
      gap: 0.75rem;
    }
  }

